UPDATE `email_templates` SET `body_html` = '<div style="background:#eef2f7;padding:28px 0 40px;font-family:Helvetica Neue,Arial,sans-serif;"><div style="max-width:600px;margin:0 auto;"><div style="background:#d97706;padding:30px 40px 26px;border-radius:12px 12px 0 0;text-align:center;">{{logo_html}}<p style="color:rgba(255,255,255,0.7);font-size:11px;letter-spacing:2px;text-transform:uppercase;margin:0 0 8px;">{{app_name}}</p><div style="font-size:36px;line-height:1;margin:0 0 8px;">&#128230;</div><h1 style="color:#fff;margin:0;font-size:23px;font-weight:700;line-height:1.3;">Υπενθύμιση Λήξης Ραφιών Αποθήκης</h1></div><div style="background:#fff;padding:36px 40px 40px;border-radius:0 0 12px 12px;box-shadow:0 4px 20px rgba(0,0,0,0.07);"><h2 style="color:#1f2937;font-size:18px;font-weight:700;margin:0 0 14px;">Γεια σας {{user_name}},</h2><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Υπάρχουν είδη αποθήκης που πλησιάζουν ή έχουν ξεπεράσει την ημερομηνία λήξης τους.</p><div style="background:#f9fafb;border-left:4px solid #d97706;padding:2px 20px;border-radius:0 8px 8px 0;margin:20px 0;"><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Ληγμένα Είδη:</span><span style="color:#111827;font-weight:600;">{{expired_count}}</span></div><div style="padding:7px 0;font-size:14px;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Κοντά σε Λήξη (εντός {{threshold_days}} ημερών):</span><span style="color:#111827;font-weight:600;">{{expiring_count}}</span></div></div><div style="background:#f9fafb;border-radius:8px;padding:16px 20px;margin:20px 0;"><p style="color:#6b7280;font-size:13px;font-weight:600;text-transform:uppercase;letter-spacing:1px;margin:0 0 8px;">Λεπτομέρειες:</p><pre style="background:#fff;border:1px solid #e5e7eb;border-radius:6px;padding:12px 16px;font-size:13px;color:#374151;margin:0;white-space:pre-wrap;word-break:break-word;">{{details}}</pre></div><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Συνδεθείτε στο σύστημα για να ελέγξετε τα είδη.</p><div style="text-align:center;margin:28px 0 4px;"><a href="{{login_url}}" style="background:#d97706;color:#ffffff;text-decoration:none;padding:13px 38px;border-radius:8px;font-size:15px;font-weight:700;display:inline-block;letter-spacing:0.3px;">Διαχείριση Αποθήκης</a></div></div><div style="text-align:center;padding:18px 0 0;color:#9ca3af;font-size:12px;line-height:1.9;"><p style="margin:0;"><strong style="color:#6b7280;">{{app_name}}</strong> &bull; Σύστημα Διαχείρισης Εθελοντών</p><p style="margin:0;">Αυτό το μήνυμα στάλθηκε αυτόματα από το σύστημα.</p></div></div></div>', `name` = 'Υπενθύμιση Λήξης Ραφιών Αποθήκης', `subject` = 'Υπενθύμιση: Είδη Αποθήκης Λήγουν ή Έχουν Λήξει', `updated_at` = NOW() WHERE `code` = 'shelf_expiry_reminder';

UPDATE `email_templates` SET `body_html` = '<div style="background:#eef2f7;padding:28px 0 40px;font-family:Helvetica Neue,Arial,sans-serif;"><div style="max-width:600px;margin:0 auto;"><div style="background:#0ea5e9;padding:30px 40px 26px;border-radius:12px 12px 0 0;text-align:center;">{{logo_html}}<p style="color:rgba(255,255,255,0.7);font-size:11px;letter-spacing:2px;text-transform:uppercase;margin:0 0 8px;">{{app_name}}</p><div style="font-size:36px;line-height:1;margin:0 0 8px;">&#128203;</div><h1 style="color:#fff;margin:0;font-size:23px;font-weight:700;line-height:1.3;">Υπενθύμιση Λήξης Πιστοποιητικού</h1></div><div style="background:#fff;padding:36px 40px 40px;border-radius:0 0 12px 12px;box-shadow:0 4px 20px rgba(0,0,0,0.07);"><h2 style="color:#1f2937;font-size:18px;font-weight:700;margin:0 0 14px;">Αγαπητέ/ή {{citizen_name}},</h2><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Σας ενημερώνουμε ότι το πιστοποιητικό σας πλησιάζει στην ημερομηνία λήξης του. Παρακαλούμε ξεκινήστε τη διαδικασία ανανέωσής του εγκαίρως.</p><div style="background:#f9fafb;border-left:4px solid #0ea5e9;padding:2px 20px;border-radius:0 8px 8px 0;margin:20px 0;"><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Πιστοποιητικό:</span><span style="color:#111827;font-weight:600;">{{certificate_type}}</span></div><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Ημ. Λήξης:</span><span style="color:#111827;font-weight:600;">{{expiry_date}}</span></div><div style="padding:7px 0;font-size:14px;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Υπόλοιπες Ημέρες:</span><span style="color:#111827;font-weight:600;">{{days_remaining}} ημέρες</span></div></div><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Διαθέτετε ακόμη αρκετό χρόνο για να ολοκληρώσετε τη διαδικασία ανανέωσης χωρίς πρόβλημα.</p><div style="text-align:center;margin:28px 0 4px;"><a href="{{login_url}}" style="background:#0ea5e9;color:#ffffff;text-decoration:none;padding:13px 38px;border-radius:8px;font-size:15px;font-weight:700;display:inline-block;letter-spacing:0.3px;">Σύνδεση στο Σύστημα</a></div></div><div style="text-align:center;padding:18px 0 0;color:#9ca3af;font-size:12px;line-height:1.9;"><p style="margin:0;"><strong style="color:#6b7280;">{{app_name}}</strong> &bull; Σύστημα Διαχείρισης Εθελοντών</p><p style="margin:0;">Αυτό το μήνυμα στάλθηκε αυτόματα από το σύστημα.</p></div></div></div>', `name` = 'Λήξη Πιστοποιητικού Πολίτη (3 μήνες)', `subject` = 'Υπενθύμιση: Το πιστοποιητικό σας λήγει σε 3 μήνες', `updated_at` = NOW() WHERE `code` = 'citizen_cert_expiry_3months';

UPDATE `email_templates` SET `body_html` = '<div style="background:#eef2f7;padding:28px 0 40px;font-family:Helvetica Neue,Arial,sans-serif;"><div style="max-width:600px;margin:0 auto;"><div style="background:#eab308;padding:30px 40px 26px;border-radius:12px 12px 0 0;text-align:center;">{{logo_html}}<p style="color:rgba(255,255,255,0.7);font-size:11px;letter-spacing:2px;text-transform:uppercase;margin:0 0 8px;">{{app_name}}</p><div style="font-size:36px;line-height:1;margin:0 0 8px;">&#9888;</div><h1 style="color:#fff;margin:0;font-size:23px;font-weight:700;line-height:1.3;">Το Πιστοποιητικό σας Λήγει Σύντομα</h1></div><div style="background:#fff;padding:36px 40px 40px;border-radius:0 0 12px 12px;box-shadow:0 4px 20px rgba(0,0,0,0.07);"><h2 style="color:#1f2937;font-size:18px;font-weight:700;margin:0 0 14px;">Αγαπητέ/ή {{citizen_name}},</h2><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Το πιστοποιητικό σας λήγει <strong>σε λιγότερο από 1 μήνα</strong>. Παρακαλούμε φροντίστε άμεσα για την ανανέωσή του.</p><div style="background:#f9fafb;border-left:4px solid #eab308;padding:2px 20px;border-radius:0 8px 8px 0;margin:20px 0;"><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Πιστοποιητικό:</span><span style="color:#111827;font-weight:600;">{{certificate_type}}</span></div><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Ημ. Λήξης:</span><span style="color:#111827;font-weight:600;">{{expiry_date}}</span></div><div style="padding:7px 0;font-size:14px;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Υπόλοιπες Ημέρες:</span><span style="color:#111827;font-weight:600;">{{days_remaining}} ημέρες</span></div></div><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Μην αφήσετε το πιστοποιητικό σας να λήξει — ξεκινήστε τη διαδικασία ανανέωσης σήμερα.</p><div style="text-align:center;margin:28px 0 4px;"><a href="{{login_url}}" style="background:#eab308;color:#ffffff;text-decoration:none;padding:13px 38px;border-radius:8px;font-size:15px;font-weight:700;display:inline-block;letter-spacing:0.3px;">Σύνδεση στο Σύστημα</a></div></div><div style="text-align:center;padding:18px 0 0;color:#9ca3af;font-size:12px;line-height:1.9;"><p style="margin:0;"><strong style="color:#6b7280;">{{app_name}}</strong> &bull; Σύστημα Διαχείρισης Εθελοντών</p><p style="margin:0;">Αυτό το μήνυμα στάλθηκε αυτόματα από το σύστημα.</p></div></div></div>', `name` = 'Λήξη Πιστοποιητικού Πολίτη (1 μήνα)', `subject` = 'Υπενθύμιση: Το πιστοποιητικό σας λήγει σε 1 μήνα', `updated_at` = NOW() WHERE `code` = 'citizen_cert_expiry_1month';

UPDATE `email_templates` SET `body_html` = '<div style="background:#eef2f7;padding:28px 0 40px;font-family:Helvetica Neue,Arial,sans-serif;"><div style="max-width:600px;margin:0 auto;"><div style="background:#f97316;padding:30px 40px 26px;border-radius:12px 12px 0 0;text-align:center;">{{logo_html}}<p style="color:rgba(255,255,255,0.7);font-size:11px;letter-spacing:2px;text-transform:uppercase;margin:0 0 8px;">{{app_name}}</p><div style="font-size:36px;line-height:1;margin:0 0 8px;">&#9888;</div><h1 style="color:#fff;margin:0;font-size:23px;font-weight:700;line-height:1.3;">Επείγουσα Υπενθύμιση — Λήξη Πιστοποιητικού</h1></div><div style="background:#fff;padding:36px 40px 40px;border-radius:0 0 12px 12px;box-shadow:0 4px 20px rgba(0,0,0,0.07);"><h2 style="color:#1f2937;font-size:18px;font-weight:700;margin:0 0 14px;">Αγαπητέ/ή {{citizen_name}},</h2><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;"><strong style="color:#dc2626;">Επείγουσα ειδοποίηση:</strong> Το πιστοποιητικό σας λήγει <strong>σε μόλις λίγες ημέρες</strong>. Απαιτείται άμεση ενέργεια για ανανέωση.</p><div style="background:#f9fafb;border-left:4px solid #f97316;padding:2px 20px;border-radius:0 8px 8px 0;margin:20px 0;"><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Πιστοποιητικό:</span><span style="color:#111827;font-weight:600;">{{certificate_type}}</span></div><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Ημ. Λήξης:</span><span style="color:#111827;font-weight:600;">{{expiry_date}}</span></div><div style="padding:7px 0;font-size:14px;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Υπόλοιπες Ημέρες:</span><span style="color:#111827;font-weight:600;">{{days_remaining}} ημέρες</span></div></div><div style="background:#fef2f2;border:1px solid #fecaca;border-radius:8px;padding:14px 20px;margin:20px 0;"><p style="color:#dc2626;font-size:14px;font-weight:600;margin:0;"><span style="font-size:16px;">⚠</span> Αν δεν ανανεωθεί εγκαίρως, το πιστοποιητικό σας θα λήξει και δεν θα είναι έγκυρο.</p></div><div style="text-align:center;margin:28px 0 4px;"><a href="{{login_url}}" style="background:#f97316;color:#ffffff;text-decoration:none;padding:13px 38px;border-radius:8px;font-size:15px;font-weight:700;display:inline-block;letter-spacing:0.3px;">Σύνδεση στο Σύστημα</a></div></div><div style="text-align:center;padding:18px 0 0;color:#9ca3af;font-size:12px;line-height:1.9;"><p style="margin:0;"><strong style="color:#6b7280;">{{app_name}}</strong> &bull; Σύστημα Διαχείρισης Εθελοντών</p><p style="margin:0;">Αυτό το μήνυμα στάλθηκε αυτόματα από το σύστημα.</p></div></div></div>', `name` = 'Λήξη Πιστοποιητικού Πολίτη (1 εβδομάδα)', `subject` = '⚠ Επείγον: Το πιστοποιητικό σας λήγει σε 1 εβδομάδα', `updated_at` = NOW() WHERE `code` = 'citizen_cert_expiry_1week';

UPDATE `email_templates` SET `body_html` = '<div style="background:#eef2f7;padding:28px 0 40px;font-family:Helvetica Neue,Arial,sans-serif;"><div style="max-width:600px;margin:0 auto;"><div style="background:#dc2626;padding:30px 40px 26px;border-radius:12px 12px 0 0;text-align:center;">{{logo_html}}<p style="color:rgba(255,255,255,0.7);font-size:11px;letter-spacing:2px;text-transform:uppercase;margin:0 0 8px;">{{app_name}}</p><div style="font-size:36px;line-height:1;margin:0 0 8px;">&#10060;</div><h1 style="color:#fff;margin:0;font-size:23px;font-weight:700;line-height:1.3;">Το Πιστοποιητικό σας Έληξε</h1></div><div style="background:#fff;padding:36px 40px 40px;border-radius:0 0 12px 12px;box-shadow:0 4px 20px rgba(0,0,0,0.07);"><h2 style="color:#1f2937;font-size:18px;font-weight:700;margin:0 0 14px;">Αγαπητέ/ή {{citizen_name}},</h2><p style="color:#4b5563;line-height:1.65;font-size:15px;margin:0 0 14px;">Σας ενημερώνουμε ότι το πιστοποιητικό σας <strong style="color:#dc2626;">έχει λήξει</strong>. Παρακαλούμε φροντίστε για την <strong>άμεση ανανέωσή</strong> του.</p><div style="background:#f9fafb;border-left:4px solid #dc2626;padding:2px 20px;border-radius:0 8px 8px 0;margin:20px 0;"><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Πιστοποιητικό:</span><span style="color:#111827;font-weight:600;">{{certificate_type}}</span></div><div style="padding:7px 0;font-size:14px;border-bottom:1px solid #f3f4f6;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Ημ. Λήξης:</span><span style="color:#dc2626;font-weight:600;">{{expiry_date}}</span></div><div style="padding:7px 0;font-size:14px;"><span style="color:#9ca3af;display:inline-block;min-width:140px;">Κατάσταση:</span><span style="background:#dc2626;color:#fff;padding:2px 10px;border-radius:4px;font-size:13px;font-weight:600;">ΛΗΓΜΕΝΟ</span></div></div><div style="background:#fef2f2;border:1px solid #fecaca;border-